#!/usr/bin/env bash

# Synopsis:
# Test the test runner by running it against a predefined solution 
# with an expected output. For running all available tests see run-tests.sh

# Arguments:
# $1: absolute path to solution folder

# Output:
# Outputs the diff of the expected test results against the actual test results
# generated by the test runner.

# Example:
# ./bin/run-test.sh /absolute/path/to/tests/compile-error/

# If any required arguments is missing, print the usage and exit
if [ -z "$1" ]; then
    echo "usage: ./bin/run-test.sh /absolute/path/to/test/folder/"
    exit 1
fi

test_dir="$1"

test_dir_name=$(basename "${test_dir}")
test_dir_path=$(realpath "${test_dir}")
results_file_path="${test_dir_path}/results.json"
expected_results_file_path="${test_dir_path}/expected_results.json"

bin/run.sh "${test_dir_name}" "${test_dir_path}" "${test_dir_path}"

# Normalize the results file
sed -i -e "s~\\\/~/~g" -e "s~${test_dir_path}~/solution~g" "${results_file_path}"

echo "${test_dir_name}: comparing results.json to expected_results.json"
diff "${results_file_path}" "${expected_results_file_path}"

if [ $? -ne 0 ]; then
    echo "\"${test_dir_name}\" failed!"
fi